Write-Host ""
Write-Host "  ******************************************************************************************"
Write-Host "  **                                                                                      **"
Write-Host "  **                                   Dirty Flasher                                      **"
Write-Host "  **                                       0.1                                            **"
Write-Host "  **                                                                                      **"
Write-Host "  ******************************************************************************************"

# List serial ports
$ports = Get-PnpDevice -class Ports -status OK | where FriendlyName -like "*COM*"

Write-Host "  **                                                                                      **"
Write-Host "  **                                 Detected COM ports:                                  **"
Write-Host "  **                                                                                      **"
Write-Host "  **______________________________________________________________________________________**"
Write-Host "  **                                                                                      **"

$i = 0
foreach ($port in $ports) {
	Write-Host "  **  [$i] $($port.FriendlyName) $(' ' * (78 - ($port.FriendlyName.length))) **"
	$i++
}

Write-Host "  **                                                                                      **"
Write-Host "  ******************************************************************************************"
Write-Host ""

$selectedPort = "COM0"
if ($i -gt 1) {
	$userInput = Read-Host -Prompt "    Input the COM port you would like to use [0-$($i - 1)]"
	if ($ports[$userInput].FriendlyName -match "COM[0-9+]") {
        $selectedPort = $matches[0]
		#Write-Host "Selected port: $($matches[0])"
	}
}
elseif ($i -eq 1) {
    if ($ports[0].FriendlyName -match "COM[0-9+]") {
        $selectedPort = $matches[0]
        #Write-Host "Defaulting to port: $($matches[0])"
    }
}
elseif ($i -eq 0) {
    Write-Host ""
    Write-Host "  ******************************************************************************************"
    Write-Host "  ******************************************************************************************"
    Write-Host "  ****                                                                                  ****"
    Write-Host "  ****                          Whoops: No COM ports detected!                          ****"
    Write-Host "  ****                                                                                  ****"
    Write-Host "  ******************************************************************************************"
    Write-Host "  ******************************************************************************************"
    Write-Host ""
    Pause
    Exit
}

Write-Host ""
Write-Host "  ******************************************************************************************"
Write-Host "  **                                                                                      **"
Write-Host "  **                              Enter download mode by:                                 **"
Write-Host "  **                                                                                      **"
Write-Host "  **______________________________________________________________________________________**"
Write-Host "  **                                                                                      **"
Write-Host "  **  1. Holding down the BOOT button.                                                    **"
Write-Host "  **  2. While the BOOT button is held, press REBOOT button.                              **"
Write-Host "  **  3. Release the BOOT button.                                                         **"
Write-Host "  **                                                                                      **"
Write-Host "  ******************************************************************************************"
Write-Host ""

$comPort = new-Object System.IO.Ports.SerialPort $selectedPort,115200,None,8,one
$comPort.Open()

while ($comPort.BytesToRead) {
    try {
        $comPort.Read()
    }
    catch {}
}

while ($true) {
    try {
        $line = $comPort.ReadLine()
        Write-Host "  " $($line)
        if ($line -match "waiting for download") {
            break;
        }

    }
    catch {
        try {
            $comPort.Open()
        }
        catch {}
    }
}

$comPort.Close()

while ($comPort.IsOpen) {
    Start-Sleep -Milliseconds 10
}

Write-Host ""
Write-Host "  __________________________________________________________________________________________"
Write-Host "  __                                                                                      __"
Write-Host "  __                                     * FLASHING *                                     __"
Write-Host "  __                       DO NOT EXIT UNTIL PROMPTED SAFE TO DO SO                       __"
Write-Host "  __                                                                                      __"
Write-Host "  __________________________________________________________________________________________"
Write-Host ""

$boot_app = Get-ChildItem -Path .\firmware\* -Name | Where-Object { $_ -match "^boot_app0\.bin" }
$bin = Get-ChildItem -Path .\firmware\* -Name | Where-Object { $_ -match "^\w+\.ino\.bin" }
$bootloader = Get-ChildItem -Path .\firmware\* -Name | Where-Object { $_ -match "^\w+\.ino\.bootloader\.bin" }
$partitions = Get-ChildItem -Path .\firmware\* -Name | Where-Object { $_ -match "^\w+\.ino\.partitions\.bin" }


if ($boot_app -ne $null -and $bin -ne $null -and $bootloader -ne $null -and $partitions -ne $null) {
    #& { ./esptool.exe --chip esp32s3 --port `"$($selectedPort)`" --baud 921600 --before default_reset --after hard_reset write_flash -e -z --flash_mode dio --flash_freq 80m --flash_size 8MB 0x0 `".\firmware\$($bootloader)`" 0x8000 `".\firmware\$($partitions)`" 0xe000 `".\firmware\$($boot_app)`" 0x10000 `".\firmware\$($bin)`" } 2>&1 |ForEach-Object {"  $_"}
    ./esptool.exe --chip esp32s3 --port `"$($selectedPort)`" --baud 921600 --before default_reset --after hard_reset write_flash -e -z --flash_mode dio --flash_freq 80m --flash_size 8MB 0x0 `".\firmware\$($bootloader)`" 0x8000 `".\firmware\$($partitions)`" 0xe000 `".\firmware\$($boot_app)`" 0x10000 `".\firmware\$($bin)`"
}
elseif ($bin -ne $null -and $bootloader -ne $null -and $partitions -ne $null) {
    #& { ./esptool.exe --chip esp32s3 --port `"$($selectedPort)`" --baud 921600 --before default_reset --after hard_reset write_flash -e -z --flash_mode dio --flash_freq 80m --flash_size 8MB 0x0 `".\firmware\$($bootloader)`" 0x8000 `".\firmware\$($partitions)`" 0x10000 `".\firmware\$($bin)`" }
    ./esptool.exe --chip esp32s3 --port `"$($selectedPort)`" --baud 921600 --before default_reset --after hard_reset write_flash -e -z --flash_mode dio --flash_freq 80m --flash_size 8MB 0x0 `".\firmware\$($bootloader)`" 0x8000 `".\firmware\$($partitions)`" 0x10000 `".\firmware\$($bin)`"
}
else {
    Write-Host ""
    Write-Host "  ******************************************************************************************"
    Write-Host "  ******************************************************************************************"
    Write-Host "  ****                                                                                  ****"
    Write-Host "  ****                         Whoops: Firmware files missing!                          ****"
    Write-Host "  ****                                                                                  ****"
    Write-Host "  ******************************************************************************************"
    Write-Host "  ******************************************************************************************"
    Write-Host ""
    Pause
    Exit
}

$espResult = $LASTEXITCODE

if ($espResult -eq 0) {
    Add-Type -Name Window -Namespace Console -MemberDefinition '
    [DllImport("Kernel32.dll")] 
    public static extern IntPtr GetConsoleWindow();
    [DllImport("user32.dll")]
    public static extern IntPtr SetWindowPos(IntPtr hWnd, int hWndInsertAfter, int x, int Y, int cx, int cy, int wFlags);'
    
    [Console]::Beep()
    [Console]::setBufferSize([Console]::LargestWindowWidth, 3000)
    [Console]::setWindowSize([Console]::LargestWindowWidth, [Console]::LargestWindowHeight)
    
    $consoleHWND = [Console.Window]::GetConsoleWindow();
    $result = [Console.Window]::SetWindowPos($consoleHWND, -1, 0, 0, 0, 0, 0x0001);
    
